import react, { useContext, useEffect } from "react";
import { CoursesContext } from '../../context/courses.context';
import { getCourses } from '../../utils/firebase/firebase.utils';
import "./home.styles.scss";

const Home = () => {
  const { courses, setCourses, tags, setTags } = useContext(CoursesContext);
    useEffect(async() => {
        const allCourses = await getCourses();
        if(courses.length === 0){
            setCourses(allCourses);
        }
    }, [])

    const listOfAllTags = [];
    courses.forEach(async(eachCourse)=>{
      await listOfAllTags.push(eachCourse.data().tag);
    });
    setTags(listOfAllTags.filter((eachTag)=> !listOfAllTags.includes(eachTag)));
    console.log(tags);
  return (
    <div className="poster">

    </div>
  );
};

export default Home;
