import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export default Notify = (type, message) => {
    switch(type){
        case 'success':
            return toast.success(message, {
                    position: toast.POSITION.TOP_RIGHT
            });
        case 'error':
            return toast.error(message, {
                position: toast.POSITION.TOP_RIGHT
            });
    }
}
