import React from 'react'

function FormInput({ label, ...inputProps }) {
    return (
        <div className="group">
            {
                label && 
                    <label className={`${inputProps.value.length ? 'shrink': '' }  form-inpur-label `}>{label}</label>

            }
            <input {...inputProps}/>
        </div>
    )
}

export default FormInput
